#include <iostream>
#include <vector>

using namespace std;

int main()
{
    int N;
    long long number;
    int sum = 0;

    cin>>N;
    cin>>number;

    for(int i=0; i < N; i++)
    {
        int calc = number % pow(10, i + 1) / pow(10, i);
        cout<<calc;
        sum += calc;
    }

    cout<<endl<<sum;

    return 0;
}

